package com;

public class ArrayProgram3 {
	
	//3.Write a Java program to print the following grid.

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=0; i<=10; i++)
		{
				for(int j=1; j<=10; j++)
				{
					System.out.print("  -");
				}
			
			
			System.out.println();
		}

	}

}
